export default async function avatar(req, res) {
  const {
    query: {
      username_slug
    }
  } = req;
  let userAvatar = `https://www.tinygraphs.com/spaceinvaders/${username_slug}?theme=heatwave&numcolors=4&size=220&fmt=svg`;

  try {
    const gravatarResponse = await fetch(`https://unavatar.now.sh/facebook/${username_slug}?json&fallback=false`).then(r => r.json());
    userAvatar = gravatarResponse.url || userAvatar; // eslint-disable-next-line no-empty
  } catch {}

  res.writeHead(302, {
    Location: userAvatar
  }).end();
}
